import { useState } from "react";

function Accordion({items}) { 
    const [expandedIndex, setExpandedIndex] = useState(0);

    const handleClick = (nextIndex) => {  //we made this outside mapping func to make map fanction easier to read
        setExpandedIndex(nextIndex);
         
    };
    const renderedItems = items.map((item, index) => {
        const isExpanded = index === expandedIndex;
        const icon = <span>
            {isExpanded ? 'Down' : 'Left'}
        </span>

        const content = isExpanded && <div>{item.content}</div>;  //&& makes me take the first false value or the last true one and js bu its nature dont print false, true null and undefined values

        return (
            <div key={item.id }>
                {item.labe}
                <div onClick={() => handleClick(index)}>{item.label}</div>
                {content}
            </div>
        );
    });

    return <div>{renderedItems}</div>
}

export default Accordion;
