    const increment = () => {
        setCount(count + 1 );
    };

    const decrement = () => {
        setCount(count - 1 );
    };

    const handleChange = (event) => {
        const value = parseInt(event.target.value) || 0;
        setValueToAdd(value); 
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        
        setCount(count + valueToAdd);
        setValueToAdd(0);
    };

    return (
        <Panel className='m-3'>
            <h1 className="text-lg"> count is {count}</h1>
            <div className="flex flex-row">
                <Button success onClick={increment}>increment</Button>
                <Button danger onClick={decrement}>decrement</Button>
            </div>

            <form onSubmit={handleSubmit}>
                <label>Add a lot!</label>
                <input type='number' value={valueToAdd || ""} onChange={handleChange} className="p-1 m-3 bg-gray-50 border border-gray-300"/>
                <Button primary>Add it</Button>
            </form>
        </Panel>
    )
}

export default CounterPage;
